mapper.insert:
INSERT INTO USER_INFO
(USER_NO, USER_ID, USER_PW, USER_NAME, USER_MAILID, USER_MAILDOMAIN, REGDATE, AUTHORITY)
VALUES (
			   (SELECT NULLIF (MAX(A.USER_NO),0)+1 FROM USER_INFO A),
		 #{user_id}, #{user_pw}, #{user_name}, #{user_mailid}, #{user_maildomain}, SYSDATE(), #{authority}
	)
